options.content:
{{!-- Verificar si hay alertas --}}
{{#if data.[0]}} 

{{#each data.[0]}}
  <div class="item">
    <div class="icono-container">
      {{#if estado_alerta}}
        <i class="fa fa-exclamation-triangle" style="color:rgb(242, 73, 92);color:{{colorTipoAlerta tipo_alerta estado_alerta}};"></i>
      {{else}}
        <i class="fa fa-check-square" style="color:rgb(115, 191, 105)"></i>
      {{/if}}
    </div>
    <div class="contenido">
      <div class="titulo">
        <div class="titulo-texto">
          {{!-- <a href="d/ddxwcck13f280b/sensor?orgId=1&var-sensor={{id_sensor}}"> --}}
            {{#if nombre_bodega}}{{nombre_bodega}} - {{/if}}
            {{nombre_sensor}}
          {{!-- </a> --}}
        </div>
      </div>
      <div class="texto">
        {{#if (esMedicion tipo_alerta)}}
          <span style="color:{{colorTipoAlerta tipo_alerta estado_alerta}};">
            {{capitalize tipo_alerta}}
          </span>
          {{capitalize tipo_medicion}} a {{valor}} {{unidad}}
        {{/if}}
        {{#if (eq tipo_alerta "bateria_baja")}}
          <span style="color:{{colorTipoAlerta tipo_alerta estado_alerta}};">
            Bater√≠a
          </span>
          a {{valor}}%
        {{/if}}
        {{#if (eq tipo_alerta "inactividad")}}
          <span style="color:{{colorTipoAlerta tipo_alerta estado_alerta}};">
            Inactividad
          </span>
          {{#if estado_alerta}}
            de {{valor}} minutos
          {{/if}}
        {{/if}}

        {{#if estado_alerta}}{{else}} - Normalizada{{/if}}
      </div>
    </div>
    <div class="fecha">
      <div>
        {{date fecha "hh:mm A"}}
      </div>
      <div>
        {{date fecha "DD/MM/YYYY"}}
      </div>
    </div>
  </div>
{{/each}}

{{else}}
<div style="font-size: 2rem;font-weight: 500;text-align: center;margin-top:20px;">
  No hay Alertas
</div>
{{/if}}

options.helpers:
context.handlebars.registerHelper("myicono", (text) =>
  'hola'
);

context.handlebars.registerHelper("esMedicion", (tipo_alerta) =>
  (tipo_alerta === 'alarma' || tipo_alerta === 'advertencia')
);

context.handlebars.registerHelper("capitalize", (string) =>
  string.charAt(0).toUpperCase() + string.slice(1)
);
context.handlebars.registerHelper("colorTipoAlerta", (tipo_alerta, estado_alerta) => {
  if (estado_alerta === false) return 'rgb(115, 191, 105)'
  if (tipo_alerta === 'alarma') return 'rgb(242, 73, 92)'
  if (tipo_alerta === 'advertencia') return 'rgb(250, 222, 43)'
  // if (tipo_alerta === 'bateria_baja') return 'rgb(86, 148, 242)'
  if (tipo_alerta === 'bateria_baja') return 'rgb(250, 222, 43)'
  // if (tipo_alerta === 'inactividad') return 'rgb(184, 118, 217)'
  if (tipo_alerta === 'inactividad') return 'rgb(242, 73, 92)'
}
);

