options.elementValueChanged:
if (context.element.id === 'estado_alerta') {
  context.grafana.locationService.partial({
    'var-estado_alerta': context.element.value
  })
  // context.grafana.locationService.refresh();
}
if (context.element.id === 'estado_sensor') {
  context.grafana.locationService.partial({
    'var-estado_sensor': context.element.value
  })
  // context.grafana.locationService.refresh();
}
if (context.element.id === 'bodega') {
  context.grafana.locationService.partial({
    'var-bodega': context.element.value
  })
  // context.grafana.locationService.refresh();
}

